<?php

declare(strict_types=1);

namespace Database\Seeders\Game\Core\Features\Spell;

use App\Books\Domain\Reader\Reader;
use Database\Seeders\Game\Core\Features\Spell\Helper\Spell;
use Garanaw\SeedableMigrations\Seeder;

class SpellSeeder extends Seeder
{
    public function run(): bool
    {
        return $this->db->table('spells')->insert($this->getData());
    }

    private function prepareCache(): void
    {
        resolve(Reader::class)->getCached();
    }

    protected function getData(): array
    {
        $this->prepareCache();

